function defaultDescription(){var e=document.getElementById("description"),t=document.getElementById("label"),s=document.getElementById("desc_error"),l=document.getElementById("post_description");if(e.className="",t.className="",s.className="hidden",""==l.value){for(var n=document.getElementById("post_category_ids"),o=[],a=0;a<n.options.length;a++)1==n.options[a].selected&&o.push(n.options[a].value);1==o.length?l.value=gon.descriptions[o[0]]:e.value=gon.default_description}else e.className="field_with_errors",t.className="field_with_errors",s.className="alert alert-danger"}